import '../styles/transaction.css';
import { Component } from 'react';
import axios from 'axios';

class Transaction extends Component {

    remove = () => {
        this.props.remove(this.props.transaction.vendor)
    }

    async saveTransaction() {
        const response = await $.post('/transaction', this.props.transaction)
        this.props.dummyData.push(response)
    }

    async removeTransaction(transactionVendor) {
        const transactionToDelete = await $.ajax({
            url: `/transaction`,
            method: "DELETE",
        })

        const vendor = transactionToDelete.vendor
        const index = this.props.dummyData.findIndex(d => d.vendor === vendor)
        if (index !== -1) this.props.dummyData.splice(index, 1);
    }


    render() {
        return (
            <div className="list-type1">
                <ol>
                    <li>
                        <a>


                            <p><span className="amount">{`
                            Amount: ${this.props.transaction.amount}`}</span>


                                <span className="vendor">{`
                            Vendor: ${this.props.transaction.vendor}`}</span>


                                <span className="category">{`
                            Category: ${this.props.transaction.category}`}</span>
                                <span className="trash"><i id="trash"onClick={this.remove} className="fas fa-trash"></i></span></p>


                        </a>
                    </li>
                </ol>
            </div>
        )
    }


}

export default Transaction;
