import './App.css';
import { Component } from 'react';
import Operations from './components/Operations';
import Transactions from './components/Transactions';

class App extends Component {

  constructor() {
    super()
    this.state = {
      dummyData: [
        { amount: 3200, vendor: "Elevation", category: "Salary" },
        { amount: -7, vendor: "Runescape", category: "Entertainment" },
        { amount: -20, vendor: "Subway", category: "Food" },
        { amount: -98, vendor: "La Baguetterie", category: "Food" }
      ],
    }
  }

  render() {
    let balance = 0
    for (let b of this.state.dummyData) {
      balance += b.amount
    }
    return (
      <div className="App">
        <div class="nav-wrapper">
          <div class="left-side">
            <div class="nav-link-wrapper">
              <div class='games'>Balance</div>
            </div>
            <div class="nav-link-wrapper">
              <div class='schedule'>about us</div>
            </div>
            <div class="nav-link-wrapper">
              <div class='news'>explore</div>
            </div>
          </div>

          <div class="right-side">
            <div class="brand">
              login
            </div>
            <div class="brand">
              get all recipes magazine
            </div>
          </div>
        </div>
        <div className="balance">
          {`Balance: $${balance}`}
        </div>
        <Transactions dummyData={this.state.dummyData} />
        <Operations />
      </div>
    );
  }


}

export default App;
