import './App.css';
import { Component } from 'react';
import Operations from './components/Operations';
import Transactions from './components/Transactions';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom'
import $ from 'jquery';



class App extends Component {

  constructor() {
    super()
    this.state = {
      dummyData: [
        // { amount: 3200, vendor: "Elevation", category: "Salary" },
        // { amount: -7, vendor: "Runescape", category: "Entertainment" },
        // { amount: -20, vendor: "Subway", category: "Food" },
        // { amount: -98, vendor: "La Baguetterie", category: "Food" }
      ],
    }
  }

  async getTransactions() {
    return $.get('/transactions')
  }

  async componentDidMount() {
    const response = await this.getTransactions()
    this.setState({ dummyData: response})
  }

  remove = (vendor) => {
    let tempDummyArr = [...this.state.dummyData]
    tempDummyArr.splice(tempDummyArr.findIndex(d => vendor === d.vendor), 1)
    this.setState({
      dummyData: tempDummyArr
    })
  }

  deposit = (amount, vendor, category) => {
    const tempDummyArr = [...this.state.dummyData]
    tempDummyArr.push({ amount, vendor, category })
    this.setState({
      dummyData: tempDummyArr
    })
  }

  withdraw = (amount, vendor, category) => {
    let tempDummyArr = [...this.state.dummyData]
    tempDummyArr.push({ amount, vendor, category })
    this.setState({
      dummyData: tempDummyArr
    })
  }

  render() {

    let balance = 0
    for (let b of this.state.dummyData) {
      balance += b.amount
    }
    return (
      <Router>
        <div className="App">
          <div className="nav-wrapper">
            <div className="left-side">
              <div className="nav-link-wrapper">
                <div className='schedule'>REACT BANK</div>
              </div>
              <div className="nav-link-wrapper">
                <Link to="/transactions">Transactions</Link>
              </div>
              <div className="nav-link-wrapper">
                <div className='news'>explore</div>
              </div>
              <div className="nav-link-wrapper">
                <div className='games'>{`Balance: $${balance}`}</div>
              </div>
            </div>

            <div className="right-side">
              <div className="brand">
                login
            </div>
            </div>
          </div>

          <Operations deposit={this.deposit} withdraw={this.withdraw} dummyData={this.state.dummyData} />
          <Route path="/transactions" exact render={() => <Transactions dummyData={this.state.dummyData} remove={this.remove} />} />
        </div>
      </Router>
    );
  }


}

export default App;
