import '../styles/operations.css';
import { Component } from 'react';

class Operations extends Component {

    constructor(props) {
        super(props)
        this.state = {
            amount: 0,
            vendor: "",
            category: "",
        }
    }

    handleChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value}, )
    }

    deposit = () => {
        this.props.deposit(2,3,4)
    }

    withdraw = () => {
        this.props.withdraw()
    }

    render() {
        return (
            <div>
                <form>
                    <div id="amount-input">
                        <input type="number" placeholder="Amount" name="amount" value={this.state.amount} onChange={this.handleChange} />
                    </div>
                    <div id="vendor-input">
                        <input type="text" placeholder="Vendor" name="vendor" value={this.state.vendor} onChange={this.handleChange} />
                    </div>
                    <div id="category-input">
                        <input type="text" placeholder="Category" name="category" value={this.state.catagory} onChange={this.handleChange} />
                    </div>
                    <div className="buttons">
                        <button className="deposit" onClick={this.deposit}>Deposit</button>
                        <button className="withdraw" onClick={this.withdraw}>Withdraw</button>
                    </div>
                </form>



            </div>


        )
    }


}

export default Operations;
